import {
  Routes,
  Route,
  useNavigationType,
  useLocation,
} from "react-router-dom";
import LandingPage from "./pages/LandingPage";
import StudLogin from "./pages/StudLogin";
import RegistrationPage from "./pages/RegistrationPage";
import SignInPage from "./pages/SignInPage";
import { useEffect } from "react";
import StudProfileupdate from "./pages/StudProfileupdate";

function App() {
  const action = useNavigationType();
  const location = useLocation();
  const pathname = location.pathname;

  useEffect(() => {
    if (action !== "POP") {
      window.scrollTo(0, 0);
    }
  }, [action, pathname]);

  useEffect(() => {
    let title = "";
    let metaDescription = "";

    switch (pathname) {
      case "/":
        title = "";
        metaDescription = "";
        break;
      case "/stud-login":
        title = "";
        metaDescription = "";
        break;
      case "/registration-page":
        title = "";
        metaDescription = "";
        break;
      case "/signin-page":
        title = "";
        metaDescription = "";
        break;
    }

    if (title) {
      document.title = title;
    }

    if (metaDescription) {
      const metaDescriptionTag = document.querySelector(
        'head > meta[name="description"]'
      );
      if (metaDescriptionTag) {
        metaDescriptionTag.content = metaDescription;
      }
    }
  }, [pathname]);

  return (
    <Routes>
      <Route path="/" element={<LandingPage />} />
      <Route path="/stud-login" element={<StudLogin />} />
      <Route path="/registration-page" element={<RegistrationPage />} />
      <Route path="/signin-page" element={<SignInPage />} />
      <Route path="/edit-profile" element={<StudProfileupdate />} />
       <Route path="/" element={<LandingPage />} />
      <Route path="/stud-profileupdate" element={<StudProfileupdate />} />
      <Route path="/student-profile" element={<StudLogin />} />
      <Route path="/registration-page" element={<RegistrationPage />} />
      <Route path="/signin-page" element={<SignInPage />} />
      <Route path="/team-lead-profile" element={<TeamLeadProfile />} />
      <Route path="/request-page" element={<RequestPage />} />
      <Route path="/view-member-page" element={<ViewMemberPage />} />
      <Route
        path="/view-assigned-task-page"
        element={<ViewAssignedTaskPage />}
      />
      <Route path="/fund-management-page" element={<FundManagementPage />} />
      <Route path="/view-component-page" element={<ViewComponentPage />} />


    </Routes>
  );
}
export default App;
