import { useState } from "react";
import ConfirmLogoutPopup from "../components/ConfirmLogoutPopup";
import PortalPopup from "../components/PortalPopup";
import AddComponentPopup from "../components/AddComponentPopup";
import RemoveComponentPopup from "../components/RemoveComponentPopup";
import { useNavigate } from "react-router-dom";

const ViewComponentPage = () => {
  const [isConfirmLogoutPopupOpen, setConfirmLogoutPopupOpen] = useState(false);
  const [isAddComponentPopupOpen, setAddComponentPopupOpen] = useState(false);
  const [isRemoveComponentPopupOpen, setRemoveComponentPopupOpen] =
    useState(false);
  const navigate = useNavigate();

  const onViewMembers1Click = () => {
    navigate("/view-member-page");
  };

  const onViewMembersClick = () => {
    const anchor = document.querySelector("[data-scroll-to='rectangle2']");
    if (anchor) {
      anchor.scrollIntoView({ block: "start", behavior: "smooth" });
    }
  };

  const onFundManagementContainerClick = () => {
    navigate("/fund-management-page");
  };

  const onFundManagementClick = () => {
    const anchor = document.querySelector("[data-scroll-to='rectangle2']");
    if (anchor) {
      anchor.scrollIntoView({ block: "start", behavior: "smooth" });
    }
  };

  const onComponentsContainerClick = () => {
    navigate("/view-component-page");
  };

  const onComponentsClick = () => {
    const anchor = document.querySelector("[data-scroll-to='rectangle2']");
    if (anchor) {
      anchor.scrollIntoView({ block: "start", behavior: "smooth" });
    }
  };

  const openConfirmLogoutPopup = () => {
    setConfirmLogoutPopupOpen(true);
  };

  const closeConfirmLogoutPopup = () => {
    setConfirmLogoutPopupOpen(false);
  };

  const onLogoutClick = () => {
    const anchor = document.querySelector("[data-scroll-to='rectangle2']");
    if (anchor) {
      anchor.scrollIntoView({ block: "start", behavior: "smooth" });
    }
  };

  const onEditProfileClick = () => {
    const anchor = document.querySelector("[data-scroll-to='rectangle2']");
    if (anchor) {
      anchor.scrollIntoView({ block: "start", behavior: "smooth" });
    }
  };

  const onRequestsContainerClick = () => {
    navigate("/request-page");
  };

  const onRequestsClick = () => {
    const anchor = document.querySelector("[data-scroll-to='rectangle2']");
    if (anchor) {
      anchor.scrollIntoView({ block: "start", behavior: "smooth" });
    }
  };

  const onRequestsContainer1Click = () => {
    navigate("/view-assigned-task-page");
  };

  const onRequests1Click = () => {
    const anchor = document.querySelector("[data-scroll-to='rectangle2']");
    if (anchor) {
      anchor.scrollIntoView({ block: "start", behavior: "smooth" });
    }
  };

  const onComponents1Click = () => {
    navigate("/team-lead-profile");
  };

  const openAddComponentPopup = () => {
    setAddComponentPopupOpen(true);
  };

  const closeAddComponentPopup = () => {
    setAddComponentPopupOpen(false);
  };

  const openRemoveComponentPopup = () => {
    setRemoveComponentPopupOpen(true);
  };

  const closeRemoveComponentPopup = () => {
    setRemoveComponentPopupOpen(false);
  };

  const onRequests2Click = () => {
    const anchor = document.querySelector("[data-scroll-to='rectangle2']");
    if (anchor) {
      anchor.scrollIntoView({ block: "start", behavior: "smooth" });
    }
  };

  return (
    <>
      <div className="relative bg-white w-full h-[950px] overflow-hidden text-center text-mini text-black font-poppins">
        <div className="absolute top-[0px] left-[0px] bg-gray-400 w-[442px] h-[950px]" />
        <b className="absolute top-[64px] left-[65px] text-13xl inline-block text-white w-[310px] h-[62px]">
          D A S H B O A R D
        </b>
        <div className="absolute top-[137.5px] left-[34.5px] box-border w-[354px] h-px border-t-[1px] border-solid border-white" />
        <div
          className="absolute top-[280px] left-[1px] flex flex-col items-start justify-start cursor-pointer"
          onClick={onViewMembers1Click}
        >
          <button
            className="cursor-pointer [border:none] p-0 bg-[transparent] w-[442px] h-[76px] flex flex-col items-center justify-center hover:bg-firebrick-200 hover:cursor-pointer"
            onClick={onViewMembersClick}
          >
            <div className="relative text-xl font-semibold font-inter text-white text-center hover:text-white">
              View Members
            </div>
          </button>
        </div>
        <div
          className="absolute top-[355px] left-[1px] flex flex-col items-start justify-start cursor-pointer"
          onClick={onFundManagementContainerClick}
        >
          <button
            className="cursor-pointer [border:none] p-0 bg-[transparent] w-[442px] h-[77px] flex flex-col items-center justify-center hover:bg-firebrick-200 hover:cursor-pointer"
            onClick={onFundManagementClick}
          >
            <div className="relative text-xl font-semibold font-inter text-white text-center hover:text-white">
              Fund Management
            </div>
          </button>
        </div>
        <div
          className="absolute top-[432px] left-[1px] bg-firebrick-600 flex flex-col items-start justify-start cursor-pointer"
          onClick={onComponentsContainerClick}
        >
          <button
            className="cursor-pointer [border:none] p-0 bg-[transparent] w-[442px] h-[76px] flex flex-col items-center justify-center hover:bg-firebrick-200 hover:cursor-pointer"
            onClick={onComponentsClick}
          >
            <div className="relative text-xl font-semibold font-inter text-white text-center flex items-center justify-center w-[154px] h-11 shrink-0 hover:text-white">
              Components
            </div>
          </button>
        </div>
        <div
          className="absolute top-[819px] left-[1px] flex flex-col items-start justify-start cursor-pointer"
          onClick={openConfirmLogoutPopup}
        >
          <button
            className="cursor-pointer [border:none] p-0 bg-[transparent] w-[442px] h-[78px] flex flex-col items-center justify-center hover:bg-firebrick-200 hover:cursor-pointer"
            onClick={onLogoutClick}
          >
            <div className="relative text-xl font-semibold font-inter text-white text-center flex items-center justify-center w-[154px] h-11 shrink-0 hover:text-white">
              Logout
            </div>
          </button>
        </div>
        <div className="absolute top-[739px] left-[1px] flex flex-col items-start justify-start">
          <button
            className="cursor-pointer [border:none] p-0 bg-[transparent] w-[442px] h-20 flex flex-col items-center justify-center hover:bg-firebrick-200 hover:cursor-pointer"
            onClick={onEditProfileClick}
          >
            <div className="relative text-xl font-semibold font-inter text-white text-center flex items-center justify-center w-[154px] h-11 shrink-0 hover:text-white">
              Edit Profile
            </div>
          </button>
        </div>
        <div
          className="absolute top-[585px] left-[1px] flex flex-col items-start justify-start cursor-pointer"
          onClick={onRequestsContainerClick}
        >
          <button
            className="cursor-pointer [border:none] p-0 bg-[transparent] w-[442px] h-[77px] flex flex-col items-center justify-center hover:bg-firebrick-200 hover:cursor-pointer"
            onClick={onRequestsClick}
          >
            <div className="relative text-xl font-semibold font-inter text-white text-center flex items-center justify-center w-[154px] h-11 shrink-0 hover:text-white">
              Requests
            </div>
          </button>
        </div>
        <div
          className="absolute top-[508px] left-[1px] flex flex-col items-start justify-start cursor-pointer"
          onClick={onRequestsContainer1Click}
        >
          <button
            className="cursor-pointer [border:none] p-0 bg-[transparent] w-[442px] h-[77px] flex flex-col items-center justify-center hover:bg-firebrick-200 hover:cursor-pointer"
            onClick={onRequests1Click}
          >
            <div className="relative text-xl font-semibold font-inter text-white text-center hover:text-white">
              View Assigned Tasks
            </div>
          </button>
        </div>
        <button
          className="cursor-pointer [border:none] p-0 bg-[transparent] absolute top-[202px] left-[0px] w-[442px] h-[77px] hover:bg-firebrick-200 hover:cursor-pointer"
          onClick={onComponents1Click}
        >
          <div className="absolute h-[57.14%] w-[34.84%] top-[21.43%] left-[32.58%] text-xl font-semibold font-inter text-white text-center flex items-center justify-center hover:text-white">
            Profile
          </div>
        </button>
        <label
          className="absolute top-[79px] left-[439px] text-13xl font-archivo-black inline-block w-[1253px] h-[61px]"
          id="studentname"
        >{`Component Inventory `}</label>
        <div className="absolute top-[137.5px] left-[482.5px] box-border w-[1145px] h-px border-t-[1px] border-solid border-dimgray-400" />
        <div className="absolute top-[237px] left-[483px] h-[41px] flex flex-row items-center justify-start text-white">
          <div className="bg-crimson-200 box-border w-[149px] h-[41px] overflow-hidden shrink-0 flex flex-col py-3 px-[3px] items-end justify-end text-left border-[1px] border-solid border-white">
            <b className="relative leading-[100%] inline-block w-[130px] h-4 shrink-0">
              Component ID
            </b>
          </div>
          <div className="bg-crimson-200 box-border w-[191px] h-[41px] overflow-hidden shrink-0 flex flex-col py-3 px-0 items-center justify-end border-[1px] border-solid border-white">
            <b className="relative leading-[100%] inline-block w-[191px] h-4 shrink-0">
              Model
            </b>
          </div>
          <div className="bg-crimson-200 box-border w-[149px] h-[41px] overflow-hidden shrink-0 flex flex-col py-3 px-0 items-center justify-end border-[1px] border-solid border-white">
            <b className="relative leading-[100%] inline-block w-[149px] h-4 shrink-0">
              Quantity
            </b>
          </div>
          <div className="bg-crimson-200 box-border w-[245px] h-[41px] overflow-hidden shrink-0 flex flex-col py-3 px-0 items-center justify-end border-[1px] border-solid border-white">
            <b className="relative leading-[100%] inline-block w-[245px] h-4 shrink-0">
              Functionality Status
            </b>
          </div>
          <div className="bg-crimson-200 box-border w-[255px] h-[41px] overflow-hidden shrink-0 flex flex-col items-center justify-center border-[1px] border-solid border-white">
            <b className="relative leading-[100%] inline-block w-[255px] h-[15px] shrink-0">
              Assigned to
            </b>
          </div>
          <div className="bg-crimson-200 box-border w-44 h-[41px] overflow-hidden shrink-0 flex flex-col py-3 px-0 items-center justify-end border-[1px] border-solid border-white">
            <b className="relative leading-[100%] inline-block w-44 h-[15px] shrink-0">
              Subteam
            </b>
          </div>
        </div>
        <div className="absolute top-[278px] left-[483px] h-[41px] flex flex-row items-center justify-start">
          <div className="bg-lightgray-100 box-border w-[149px] h-[41px] overflow-hidden shrink-0 flex flex-col py-3 px-0 items-start justify-end text-gray-500 border-[1px] border-solid border-white">
            <b className="relative leading-[100%] inline-block w-[146px] h-4 shrink-0">
              C1
            </b>
          </div>
          <div className="bg-lightgray-100 box-border w-[191px] h-[41px] overflow-hidden shrink-0 flex flex-col py-3 px-0 items-center justify-end border-[1px] border-solid border-white">
            <div className="relative leading-[100%] inline-block w-[191px] h-4 shrink-0">
              BMP180
            </div>
          </div>
          <div className="bg-lightgray-100 box-border w-[149px] h-[41px] overflow-hidden shrink-0 flex flex-col py-3 px-0 items-center justify-end border-[1px] border-solid border-white">
            <div className="relative leading-[100%] inline-block w-[149px] h-4 shrink-0">
              1
            </div>
          </div>
          <div className="bg-lightgray-100 box-border w-[245px] h-[41px] overflow-hidden shrink-0 flex flex-col py-3 px-0 items-center justify-end border-[1px] border-solid border-white">
            <div className="relative leading-[100%] font-medium inline-block w-[245px] h-4 shrink-0">
              Working
            </div>
          </div>
          <div className="bg-lightgray-100 box-border w-[255px] h-[41px] overflow-hidden shrink-0 flex flex-col py-[11px] px-0 items-start justify-start border-[1px] border-solid border-white">
            <div className="relative leading-[100%] inline-block w-[235px] h-[15px] shrink-0">
              Jawadur Rahman
            </div>
          </div>
          <div className="bg-lightgray-100 box-border w-44 h-[41px] overflow-hidden shrink-0 flex flex-col py-3 px-0 items-center justify-start border-[1px] border-solid border-white">
            <div className="relative leading-[100%] inline-block w-44 h-[15px] shrink-0">
              Mechanical
            </div>
          </div>
